classdef defaults
    %defaults contains a variety of default settings and configurations for the
    %MATLAB MSOT pipeline, to enable easier testing and configuration of future
    %runs. 
    
    properties
        contents;
    end
    
    methods
        function obj = defaults(varargin)
           obj.contents = varargin;
        end
        
        function cels = cell(obj)
           cels = {obj.contents{:}}; 
        end
        
        function c = struct(obj)
           if isstruct(obj.contents{:})
               c = obj.contents{:};
           else
               c = obj.contents{:};
           end
        end
        
        function out = unpack(obj)
            out = obj.contents{:};
        end
        
                
    end
    
    enumeration
        %% Paths 
        defaultPath (fileparts(fileparts(mfilename('fullpath'))))
        MSOTBEANSPATH  (fullfile(unpack(util.defaults.defaultPath),'external/msotbeans.jar'));
        XBEANPATH (fullfile(unpack(util.defaults.defaultPath),'external/xbean.jar'));
        USER_SPECTRA_FOLDER ('');
        SYSTEM_SPECTRA_FOLDER (fullfile(unpack(util.defaults.defaultPath),'external/spectra'));
        PARALLEL_PROFILE ('');
        
        
        
        %% ReplaceVals phase index
        ReplacementPhaseIndex (struct('Construction',{'default:'},...               % On the creation of the System, after setProperties is invoked.
                                      'Initialization',{'meta:','eval:','obj:'},...  % During setup (setupImpl)
                                      'Operation',{'frame:'} )           )          % On each run, during step (stepImpl)
        
        
                                  
                                  
        %% Individual default settings
        defaultReconModel ('backproject2D')
        defaultReconSolver ('lsqr')
        defaultFieldOfView ('30')
        defaultNumPixels ('250')
                     
        
        %% Composite settings
        PipelineSettings (struct('loaderSettings',cell(util.defaults.LoaderSettings),...
                                'filterSettings',cell(util.defaults.FilterSettings),...
                                'reconSettings',cell(util.defaults.ReconSettings),...
                                'debugSettings',cell(util.defaults.DebugSettings),...
                                'parallelSettings',cell(util.defaults.ParallelSettings),...
                                'outputSettings',cell(util.defaults.OutputSettings),...
                                'unmixSettings',cell(util.defaults.UnmixSettings)))
        
        
        LoaderSettings (struct('UndoScalingCorrection',1,...
                               'UndoLaserEnergyCorrection', 0,...
                               'MsotFile','meta:XMLFileName'));
        
        FilterSettings (struct('N_Samples'      ,'meta:MeasurementDesc.RecordLength',...
                                'N_Transducers'         ,'meta:HWDesc.NumDetectors',...
                                'SampleRate'            ,'meta:HWDesc.SamplingFrequency',...
