                                'HpFilterRipple'        ,0.1,...
                                'HpFilterAttenuation'   ,40,...
                                'LpFilterFrequency'     ,7E6,...
                                'LpFilterOrder'         ,4,...
                                'LpFilterRipple'        ,0.1,...
                                'LpFilterAttenuation'   ,40,...
                                'WienerLambda'          ,0.051,...
                                'WaterAbsorptionCoeffs' ,'meta:MeasurementDesc.WaterAbsorptionCoeff',...
                                 'PathLengthInWater'     ,'meta:MeasurementDesc.PathLengthInWater',...
                                'LowpassFilterArgs'     ,cell(util.defaults.DigitalFIRLowpassFilterSettings),...
                                'HighpassFilterArgs'     ,cell(util.defaults.DigitalFIRHighpassFilterSettings)) );          
                    
                    
        
        AlternativeFilterSettings (struct('N_Samples'      ,'meta:MeasurementDesc.RecordLength',...
                                            'N_Transducers'         ,'meta:HWDesc.NumDetectors',...
                                            'SampleRate'            ,'meta:HWDesc.SamplingFrequency',...
                                            'SignalRegion'          ,[false(550,1); true(999,1);false(481,1)],...
                                            'IgnoreRegion'          ,[true(50,1);false(1980,1)],...
