                                            'LpFilterAttenuation'   ,20,...
                                            'WienerLambda'          ,0.05) );   
                                        
                                        
        DigitalFIRHighpassFilterSettings ({'designfilt','highpassfir', ...        
                                'FilterOrder',4,...
                                'PassbandFrequency',5E3, ...
                                'StopbandFrequency',4.5E3, ...
                                'SampleRate','obj:SampleRate'}.');
                            
        DigitalFIRLowpassFilterSettings ({'designfilt','lowpassfir', ...        
                                'FilterOrder',4,...
