                                'StopbandFrequency','eval:((obj.SampleRate)*0.1925)', ...
                                'SampleRate','obj:SampleRate'}.');
                            
                            
        DigitalIIRHighpassFilterSettings ({'designfilt','highpassfir', ...        
                                'FilterOrder',4,...
                                'PassbandFrequency',5E3, ...
                                'PassbandRipple',0.1, ...
